import React, { Component } from 'react';
import { withRouter } from 'react-router-dom';
import { compose } from 'recompose';
import './style.css'

import { withFirebase } from '../Firebase';
import SignInPage from "../SignIn";

const HomePage = () => (
    <div>
        <Home/>
    </div>
);

class HomePageBase extends Component {
    constructor(props) {
        super(props);

        this.cartState = "No item in your card";
        this.cartItem = [];
    }

    onChange = event => {
        this.setState({ [event.target.name]: event.target.value });
    };

    createRandomCart = () => {
        let rand =  1 + (Math.random() * (5-1));
        let items = [
            "T-Shirt",
            "Jean",
            "Basket",
            "Socks",
            "Glasses",
            "Hat",
            "Pull",
            "Watch",
        ];
        this.cartState = "";

        for (let i = 0; i < rand; ++i) {
            let pos = Math.round(Math.random() * 7);
            this.cartItem.push(items[pos]);
            this.cartState += "1 " + items[pos] + " ";
        }
        this.setState({ [""]: "" });
    };

    render() {
        const cartState = this.cartState;
        const isInvalid = cartState === "No item in your card";
        return (
            <div>
                <div className="randomCart">
                    {cartState && <p name="cartState">{cartState}</p>}
                    <div className="buttonCart">
                        <button className="randomBtn" onClick={this.createRandomCart}>Create a random cart</button>
                        <button disabled={isInvalid} className="validateBtn">Validate cart</button>
                    </div>
                </div>
                <hr />
            </div>
        )
    }
}

const Home = compose(
    withRouter,
    withFirebase,
)(HomePageBase);

const condition = authUser => !!authUser;

export default HomePage;

export { Home };
